// set variables
var centerVector = new THREE.Vector3(0, 0, 0);
var windowHalfX = window.innerWidth / 2;
var windowHalfY = window.innerHeight / 2;
var mouseX = 0, mouseY = 0;
// var texture = new THREE.TextureLoader().load("data:image/png;base64,"); 
var time = 0;

// create instance of three.scene and sets up fog
var scene = new THREE.Scene();
scene.fog = new THREE.FogExp2( 0x000000, 0.0008 );

// create instance on three.perspective camera and sets camera position
var camera = new THREE.PerspectiveCamera( 75, window.innerWidth / window.innerHeight, 1, 2000);
camera.position.z = 1500;

// creates instance of three.webglrenderer and sets size then appends the renderer
var renderer = new THREE.WebGLRenderer();
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);

// creates instance of three.buffergeometry
var geometry = new THREE.BufferGeometry();

// creates uniform variables for material and shader use
// Im having trouble adding the texture through the frag shader
// because of that I had to scale the pixel size down because
// any larger than 1 and they are squares
uniforms = {
  // texture: {type: "t", value: texture},
  a: {type: "f", value: 0.0},
  b: {type: "f", value: 0.0},
  c: {type: "f", value: 0.0},
  d: {type: "f", value: 0.0},
  size: {type: "f", value: .5}
};

// creates and sets up instance of three.shadermaterial
var shaderMaterial = new THREE.ShaderMaterial( {
  uniforms: uniforms,
  vertexShader: document.getElementById('vertexshader').textContent,
  fragmentShader: document.getElementById('fragmentshader').textContent,
  blending: THREE.NormalBlending,
  depthTest: true,
  transparent: false
});

// creates init function to run on loadup
function init() {
  // create 75000 random point positions
  // because I need enough particles to create 
  // the images and some extra and when I got 
  // the imagadata on the images they all came
  // out to around 65000-70000 approximately
  var points = [];
  for(i=0;i<75000;i++) {
    points.push((Math.random()*3000-1500));
  }
  
  // grab image data's and random positions and set to buffer variables for vertex shader use
  // dry this up
  var points = new Float32Array(points);
  var image_data_1 = createParticleImage('data:image/png;base64,')
  var image_data_2 = createParticleImage('data:image/png;base64,');
  var image_data_3 = createParticleImage('data:image/png;base64,');
  
  geometry.addAttribute('firstPosition', new THREE.BufferAttribute(image_data_1, 3));
  geometry.addAttribute('secondPosition', new THREE.BufferAttribute(image_data_2, 3));
  geometry.addAttribute('thirdPosition', new THREE.BufferAttribute(image_data_3, 3));
  geometry.addAttribute('position', new THREE.BufferAttribute(points, 3));

  // creates instance of THREE.js Points Object and adds it to the scene
  var point_cloud = new THREE.Points(geometry, shaderMaterial);
  scene.add(point_cloud);

  // creates scene and adds event listeners
  document.addEventListener('mousemove', onDocumentMouseMove, false);
  window.addEventListener('resize', onWindowResize, false);
  animate(point_cloud);
}

// function to create emoticon images
function createParticleImage(url) {
  var image_particles = []
  var canvas = document.createElement("canvas");
  var image = document.createElement("img");
  image.src = url;
  image.width = 2000;
  image.height = 2000;
  canvas.width = image.height;
  canvas.height = image.width;

  var ctx = canvas.getContext("2d");
  ctx.drawImage(image, 0, 0);

  var imagedata = ctx.getImageData(0, 0, image.width, image.height);

  // loops through imagedata to grab all the white pixels x and y coordinates
  // pushes images x and y coordinates and a range of z indexes into image_particles array
  for (var y = 0, y2 = imagedata.height; y < y2; y++) {
    for (var x = 0, x2 = imagedata.width; x < x2; x++) {
      if (imagedata.data[(x + y * imagedata.width)] > 1) {
        image_particles.push(((x - imagedata.width / 2)+ 500), ((-y + imagedata.height / 2)- 500), (Math.random()*50));
      }
    }
  }

  // loops until the image_particles length is equal to the length
  // of the original points variable length, which is 75000
  while(image_particles.length !== 75000) {
    image_particles.push((Math.random()*3000-1500));
  }

  // returns the image_particles array which is now the vertices 
  // of the image with any extra particles given random positions
  return new Float32Array(image_particles);
}

// function to handle browser page resizing
function onWindowResize() {
  windowHalfX = window.innerWidth/2;
  windowHalfY = window.innerHeight/2;
  camera.aspect = window.innerWidth/window.innerHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
}

// function to allow camera movement on mousemove
function onDocumentMouseMove(event) {
  mouseX = event.clientX - windowHalfX;
  mouseY = event.clientY - windowHalfY;
}

// funtion to render scene and camera
function animate() {
  requestAnimationFrame(animate);

  // moves the camera angle based on where the clients pointer is
  // opperates in tandem with onDocumentMouseMove function
  camera.position.x += (mouseX- camera.position.x) * 0.1;
  camera.position.y += (-mouseY- camera.position.y) * 0.1;
  

  // scales time to mess with uniform variables which changes the 
  // vertex position because of the vertex shader equation

  // need to stop the time variable from continuing to add
  // but probably just need a better way to increment the variables

  // when a=0 and b,c,d=0 particles are at original random position
  // when a,b=1 and c,d=0 particles are at first image vertices position
  // when a,c=1 and b,d=0 particles are at second image vertices position
  // when a,d=1 and b,c=0 particles are at third image vertices position

  // scaling by .01 creates the fluid movement
  // and because the particle replacement happens right on the gpu
  // it can handle the large amount of particles with ease
  if (time || time == 0) {
    time += 0.01
    if (time < 1) {
      uniforms.a.value += 0.01;
      uniforms.b.value += 0.01;
    } else if (time > 2.5 && time < 3.5) {
      uniforms.a.value -= 0.01;
      uniforms.b.value -= 0.01;
    } else if (time > 5 && time < 6) {
      uniforms.a.value += 0.01;
      uniforms.c.value += 0.01;
    } else if (time > 7.5 && time < 8.5) {
      uniforms.a.value -= 0.01;
      uniforms.c.value -= 0.01;
    } else if (time > 10 && time < 11) {
      uniforms.a.value += 0.01;
      uniforms.d.value += 0.01;
    } else if (time > 12.5 && time < 13.5) {
      uniforms.a.value -= 0.01;
      uniforms.d.value -= 0.01;
    } else if (time > 14.5) {
      uniforms.a.value = 0.0;
      uniforms.b.value = 0.0;
      uniforms.c.value = 0.0;
      uniforms.d.value = 0.0;
      time = 0;
    }
  }

  // tells the camera to center on the vec3(0,0,0) or the center of the scene
  // and renders the scene and camera
  camera.lookAt(centerVector);
  renderer.render(scene,camera);
}

init();